/**
=========================================================
* Soft UI Dashboard PRO React - v2.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/soft-ui-dashboard-pro-material-ui
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

// @mui material components
// import Card from "@mui/material/Card";
import Grid from "@mui/material/Grid";
// import { Link } from 'react-router-dom';
// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
import SuiTypography from "components/SuiTypography";
import FormField from "layouts/pages/account/components/FormField";
// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
// import Footer from "examples/Footer";
import Card from "@mui/material/Card";
import 'bootstrap/dist/css/bootstrap.min.css';
// import { F } from 'react-bootstrap';
// import { Table, Form, Col, Button, Row } from 'react-bootstrap';
// import Userclient from "layouts/dashboards/userclient/Userclient";
import SuiButton from "components/SuiButton";
import SuiTagInput from "components/SuiTagInput";
// import selectData from "layouts/pages/account/settings/components/BasicInfo/data/selectData";

import { useState } from "react";

import SuiSelect from "components/SuiSelect";
// import FormField from "layouts/pages/account/components/FormField";

function ChangePasswor() {

    const [salesDropdownValue, setSalesDropdownValue] = useState("6 May - 7 May");
    const [customersDropdownValue, setCustomersDropdownValue] = useState("6 May - 7 May");
    const [revenueDropdownValue, setRevenueDropdownValue] = useState("6 May - 7 May");
  
    // DefaultStatisticsCard state for the dropdown action
    const [salesDropdown, setSalesDropdown] = useState(null);
    const [customersDropdown, setCustomersDropdown] = useState(null);
    const [revenueDropdown, setRevenueDropdown] = useState(null);
  
    // DefaultStatisticsCard handler for the dropdown action
    const openSalesDropdown = ({ currentTarget }) => setSalesDropdown(currentTarget);
    const closeSalesDropdown = ({ currentTarget }) => {
      setSalesDropdown(null);
      setSalesDropdownValue(currentTarget.innerText || salesDropdownValue);
    };
    const openCustomersDropdown = ({ currentTarget }) => setCustomersDropdown(currentTarget);
    const closeCustomersDropdown = ({ currentTarget }) => {
      setCustomersDropdown(null);
      setCustomersDropdownValue(currentTarget.innerText || salesDropdownValue);
    };
    const openRevenueDropdown = ({ currentTarget }) => setRevenueDropdown(currentTarget);
    const closeRevenueDropdown = ({ currentTarget }) => {
      setRevenueDropdown(null);
      setRevenueDropdownValue(currentTarget.innerText || salesDropdownValue);
    };

    const renderMenu = (state, close) => (
        <Menu
          anchorEl={state}
          transformOrigin={{ vertical: "top", horizontal: "center" }}
          open={Boolean(state)}
          onClose={close}
          keepMounted
          disableAutoFocusItem
        >
          <MenuItem onClick={close}>Last 7 days</MenuItem>
          <MenuItem onClick={close}>Last week</MenuItem>
          <MenuItem onClick={close}>Last 30 days</MenuItem>
        </Menu>
      );

    const [skills, setSkills] = useState(["react", "angular"]);
    const passwordRequirements = [
        // "One special characters",
        // "Min 6 characters",
        // "One number (2 are recommended)",
        // "Change it often",
    ];

    const renderPasswordRequirements = passwordRequirements.map((item, key) => {
        const itemKey = `element-${key}`;

        return (

            <SuiBox key={itemKey} component="li" color="text" fontSize="1.25rem" lineHeight={1}>
                <SuiTypography
                    variant="button"
                    textColor="text"
                    fontWeight="regular"
                    verticalAlign="middle"
                >
                    {item}
                </SuiTypography>
            </SuiBox>
        );
    });

    return (

        <DashboardLayout>
            <DashboardNavbar />
            <Card id="change-password">
                <SuiBox p={3}>
                    <SuiTypography variant="h5">User(960)</SuiTypography>
                </SuiBox>
                {/* <SuiBox component="form" pb={3} px={3}> */}
                {/* <Grid container spacing={3}>
                        <Grid item xs={12}>
                            <FormField
                                label="Name/Email/Number/ClientID/FE UserId/PAN No"
                                placeholder="Name or Number"
                                inputProps={{ type: "password", autoComplete: "" }}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <SuiBox mb={3} ml={0.5} lineHeight={0} display="inline-block">
                                <SuiTypography
                                    component="label"
                                    variant="caption"
                                    fontWeight="bold"
                                    textTransform="capitalize"
                                >

                                    Download Excel
                                </SuiTypography>
                            </SuiBox>
                            <SuiSelect
                                placeholder="Select"
                                options={[
                                    { value: "Select ", label: "Select" },
                                    { value: "Download User", label: "Download User" },
                                    { value: "Download User Address", label: "Download User Address" },
                                    { value: "your question", label: "", isDisabled: true },
                                ]}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <SuiButton variant="gradient" buttonColor="dark"  >
                                Submit
                            </SuiButton>
                        </Grid>
                    </Grid> */}
                {/* <SuiBox mt={6} mb={1}>
                        <SuiTypography variant="h5">Password requirements</SuiTypography>
                    </SuiBox> */}
                {/* <SuiBox mb={1}>
                        <SuiTypography variant="body2" textColor="text">
                            Please follow this guide for a strong password
                        </SuiTypography>
                    </SuiBox> */}
                <SuiBox display="flex" justifyContent="space-between" alignItems="flex-end" flexWrap="wrap">
                    <SuiBox component="ul" m={0} pl={3.25} mb={{ xs: 8, sm: 0 }}>
                        {renderPasswordRequirements}
                    </SuiBox>

                </SuiBox>

                {/* </SuiBox> */}
                <SuiBox component="form" pb={3} px={3}>

                    <Grid container spacing={3}>

                        {/* <Grid item xs={12} sm={6}>
                            <FormField label="first name" placeholder="Alec" />
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <FormField label="last name" placeholder="Thompson" />
                        </Grid> */}

                        <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={12} sm={4}>
                                    <SuiBox
                                        display="flex"
                                        flexDirection="column"
                                        justifyContent="flex-end"
                                        height="100%"
                                    >
                                        <SuiBox mb={1} ml={0.5} lineHeight={0} display="inline-block">
                                            <SuiTypography
                                                component="label"
                                                variant="caption"
                                                fontWeight="bold"
                                                textTransform="capitalize"
                                            >
                                                Download Excel
                                            </SuiTypography>
                                        </SuiBox>
                                        <SuiSelect
                                            placeholder="Select"
                                            options={[
                                                { value: "Select ", label: "Select" },
                                                { value: "Download User", label: "Download User" },
                                                { value: "Download User Address", label: "Download User Address" },
                                                { value: "your question", label: "", isDisabled: true },
                                            ]}
                                        />
                                    </SuiBox>
                                </Grid>
                                <Grid item xs={12} sm={8}>
                                    <Grid container spacing={3}>
                                        <Grid item xs={12} sm={5}>
                                            <SuiBox
                                                display="flex"
                                                flexDirection="column"
                                                justifyContent="flex-end"
                                                height="100%"
                                            // style={{width:'450px',marginLeft:'40px'}}
                                            >
                                                <SuiBox mb={1} ml={1} lineHeight={0} display="inline-block">
                                                    <SuiTypography
                                                        component="label"
                                                        variant="caption"
                                                        fontWeight="bold"
                                                        textTransform="capitalize"

                                                    >
                                                        Name/Email/Number/ClientID/FE UserId/PAN No
                                                    </SuiTypography>
                                                </SuiBox>
                                                <FormField

                                                    placeholder="Name or Number"
                                                    inputProps={{ type: "password", autoComplete: "" }}

                                                />
                                            </SuiBox>
                                        </Grid>

                                        <Grid item xs={12} sm={4}>
                                            <SuiBox
                                                display="flex"
                                                flexDirection="column"
                                                justifyContent="flex-end"
                                                height="100%"
                                            >
                                                <SuiButton variant="gradient" buttonColor="dark" >
                                                    Submit
                                                </SuiButton>
                                            </SuiBox>
                                        </Grid>
                                        {/* <Grid item xs={12} sm={3}>
                                            <SuiBox
                                                display="flex"
                                                flexDirection="column"
                                                justifyContent="flex-end"
                                                height="100%"
                                            >
                                                <SuiSelect placeholder={2021} options={selectData.years} />
                                            </SuiBox>
                                        </Grid> */}
                                    </Grid>
                                </Grid>
                            </Grid>
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <FormField
                                label="email"
                                placeholder="example@email.com"
                                inputProps={{ type: "email" }}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <FormField
                                label="confirmation email"
                                placeholder="example@email.com"
                                inputProps={{ type: "email" }}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <FormField label="your location" placeholder="Sydney, A" />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <FormField
                                label="phone number"
                                placeholder="+40 735 631 620"
                                inputProps={{ type: "number" }}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <FormField label="language" placeholder="English" />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <SuiBox display="flex" flexDirection="column" justifyContent="flex-end" height="100%">
                                <SuiTagInput
                                    tags={skills}
                                    placeholder=" "
                                    onChange={(newSkill) => setSkills(newSkill)}
                                    removeOnBackspace
                                />
                            </SuiBox>
                        </Grid>
                    </Grid>
                </SuiBox>
                <SuiBox mb={3}>
                    <Grid container spacing={3}>
                        <Grid item xs={12} sm={4}>
                            <DefaultStatisticsCard
                                title="sales"
                                count="$230,220"
                                percentage={{
                                    color: "success",
                                    value: "+55%",
                                    label: "since last month",
                                }}
                                dropdown={{
                                    action: openSalesDropdown,
                                    menu: renderMenu(salesDropdown, closeSalesDropdown),
                                    value: salesDropdownValue,
                                }}
                            />
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <DefaultStatisticsCard
                                title="customers"
                                count="3.200"
                                percentage={{
                                    color: "success",
                                    value: "+12%",
                                    label: "since last month",
                                }}
                                dropdown={{
                                    action: openCustomersDropdown,
                                    menu: renderMenu(customersDropdown, closeCustomersDropdown),
                                    value: customersDropdownValue,
                                }}
                            />
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <DefaultStatisticsCard
                                title="avg. revenue"
                                count="$1.200"
                                percentage={{
                                    color: "secondary",
                                    value: "+$213",
                                    label: "since last month",
                                }}
                                dropdown={{
                                    action: openRevenueDropdown,
                                    menu: renderMenu(revenueDropdown, closeRevenueDropdown),
                                    value: revenueDropdownValue,
                                }}
                            />
                        </Grid>
                    </Grid>
                </SuiBox>
            </Card>

        </DashboardLayout>
    );
}

export default ChangePasswor;
