import { useState, useEffect } from "react";

// @mui material components

import Grid from "@mui/material/Grid";
// import AppBar from "@mui/material/AppBar";
// import Tabs from "@mui/material/Tabs";
// import Tab from "@mui/material/Tab";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
// import SuiTypography from "components/SuiTypography";
// import MiniStatisticsCard from "examples/Cards/StatisticsCards/MiniStatisticsCard";
// import SuiAvatar from "components/SuiAvatar";

// Soft UI Dashboard PRO React example components
// import DashboardNavbar from "examples/Navbars/DashboardNavbar";

// Soft UI Dashboard PRO React icons
// import Cube from "examples/Icons/Cube";
// import Document from "examples/Icons/Document";
// import Settings from "examples/Icons/Settings";

// Soft UI Dashboard PRO React base styles
import breakpoints from "assets/theme/base/breakpoints";

// Custom styles for Header
// import styles from "layouts/pages/profile/components/Header/styles";

// Images
// import burceMars from "assets/images/bruce-mars.jpg";
import { Table } from 'react-bootstrap';

function Header() {
  const [tabsOrientation, setTabsOrientation] = useState("horizontal");
  // const [tabValue, setTabValue] = useState(0);
  // const classes = styles();
  // const { values } = breakpoints;

  useEffect(() => {
    // A function that sets the orientation state of the tabs.
    function handleTabsOrientation() {
      return window.innerWidth < breakpoints.values.sm
        ? setTabsOrientation("vertical")
        : setTabsOrientation("horizontal");
    }
    /** 
     The event listener that's calling the handleTabsOrientation function when resizing the window.
    */
    window.addEventListener("resize", handleTabsOrientation);

    // Call the handleTabsOrientation function to set the state with the initial value.
    handleTabsOrientation();

    // Remove event listener on cleanup
    return () => window.removeEventListener("resize", handleTabsOrientation);
  }, [tabsOrientation]);

  // const handleSetTabValue = (event, newValue) => setTabValue(newValue);

  return (
    <SuiBox position="relative">
      {/* <DashboardNavbar absolute light /> */}
      {/* <SuiBox customClass={classes.profileHeader_background} /> */}

      <Grid py={10}>
        <Grid item xs={12} md={10} lg={12} py={3}>
          <Grid item xs={15} lg={12}>
            <SuiBox mb={3}>

              <Table striped bordered hover size="sm" style={{ textAlign: "center" }}>
                <thead>
                  <tr>
                    <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px", borderColor: 'white', width: '200px' }}>AMC Wise SIP</th>
                    <th style={{ padding: "15px", borderColor: 'white', width: '200px' }}>Scheme Wise SIP</th>
                    <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px", borderColor: 'white', width: '200px' }}>Client Wise SIP</th>
                    <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px", borderColor: 'white', width: '200px' }}>SIP Maturing In 1 Month</th>
                    <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px", borderColor: 'white', width: '200px' }}>SIP Started In Last 1 Month</th>

                  </tr>
                </thead>
              </Table>
              <br/>
              <Table striped bordered hover size="sm" style={{ textAlign: "center", borderColor: 'black' }}>
                <thead>
                  <tr>
                    <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Scheme Name</th>
                    <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>No Of SIPS</th>
                    <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>SIP Input</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td style={{ Color: "#038d8a", padding: "10px" }}>Balanced Advantage Fund Growth</td>
                    <td>1</td>
                    <td>30,000</td>
                  </tr>
                  <tr>
                    <td style={{ fontWeight: "900", backgroundColor: "#ddd", padding: "10px" }}>Total</td>
                    <td style={{ fontWeight: "900", backgroundColor: "#ddd" }}>1</td>
                    <td style={{ fontWeight: "900", backgroundColor: "#ddd" }}>30,000</td>
                  </tr>
                </tbody>
              </Table>
            </SuiBox>
          </Grid>
          <Grid item xs={12} sm={3}>
            <SuiBox mb={3}>
              {/* <MiniStatisticsCard
                    title={{ text: "SIP Maturing in 1 Month" }}
                    // count="Rs.3,462"
                    percentage={{ color: "error", text: "" }}
                    icon={{ color: "", component: "" }}
                    backgroundColor="rgb(128, 128, 128);"
                  /> */}
              {/* <rect style={{x:"318", y: "0", width:"500", height:"500", stroke:"none", strokeWidth:"0", fill:"#ffffff"}}>hy</rect> */}
            </SuiBox>
            <SuiBox mb={3}>
              {/* <MiniStatisticsCard
                    title={{ text: "Total Div Rein." }}
                    count="Rs.103,430"
                    percentage={{ color: "success", text: "" }}
                    icon={{
                      color: "",
                      component: "",
                    }}
                    // backgroundColor="#1798A5;"
                  /> */}
            </SuiBox>
          </Grid>
        </Grid>
        <Grid container spacing={3}>
          {/* <Grid item xs={12} lg={5}>
              <ReportsBarChart
                title="active users"
                description={
                  <>
                    (<strong>+23%</strong>) than last week
                  </>
                }
                chart={chart}
                items={items}
              />
            </Grid> */}
          <Grid item xs={12} lg={7}>
            {/* <GradientLineChart
                title="Sales Overview"
                description={
                  <SuiBox display="flex" alignItems="center">
                    <SuiBox fontSize={size.lg} color="success" mb={0.3} mr={0.5} lineHeight={0}>
                      <Icon className=" font-bold">arrow_upward</Icon>
                    </SuiBox>
                    <SuiTypography variant="button" textColor="text" fontWeight="medium">
                      4% more{" "}
                      <SuiTypography variant="button" textColor="text" fontWeight="regular">
                        in 2021
                      </SuiTypography>
                    </SuiTypography>
                  </SuiBox>
                }
                chart={gradientLineChartData}
              /> */}
          </Grid>
        </Grid>
      </Grid>
    </SuiBox>

  );
}

export default Header;
