// @mui material components
import Card from "@mui/material/Card";
import Grid from "@mui/material/Grid";
// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
import SuiTypography from "components/SuiTypography";
// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import DataTable from "examples/Tables/DataTable";
// import opportunity from "layouts/authentication/error/opportunity";
import ivancik from "assets/images/ivancik.jpg";
import BackgroundBlogCard from "examples/Cards/BlogCards/BackgroundBlogCard";
import datatable1 from "layouts/authentication/error/datatable1";


function Opportunity() {
    const columns =
    {
        columns: [
            { Header: "Investor Name", accessor: "InvestorName" },
            { Header: "BSE UCC:", accessor: "Location" },
            { Header: "PAN No", accessor: "Investments" },
        ],

        rows: [
            {
                InvestorName: "AGARWAL BUILDCON",
                Location: "A3832",
                Investments: "ABGFA5273N",
            },

            {
                InvestorName: "Anil Kumar Rathi",
                Location: "	A182040",
                Investments: "AASPR7023C",
            },
        ],

    }

    return (
        <DashboardLayout>
            <DashboardNavbar />
            <SuiBox my={3}>

                <SuiBox
                    backgroundColor="dark"
                    borderRadius="lg"
                    boxShadow="lg"
                    p={2}
                    backgroundGradient
                    lineHeight={1}
                >
                    <SuiTypography variant="h6" fontWeight="medium" textColor="white">
                        Questions about security?
                    </SuiTypography>
                    <SuiBox mb={3}>
                        <SuiTypography variant="button" fontWeight="regular" textColor="white">
                            Have a question, concern, or comment about security? Please contact us.
                        </SuiTypography>
                    </SuiBox>
                
                </SuiBox>

                <SuiBox mb={3}>
                    <Grid container spacing={3} alignItems="center">
                        <Grid item xs={12} lg={12}>

                            <BackgroundBlogCard
                                image={ivancik}
                                title="ELSS Opportunity Report"
                                description={
                                    <>
                                        *This report shows ELSS investment Opportunity available with all the KYC verified users who have invested less than 80C limit in current year till date.
                                    </>
                                }
                                buttonText="Go to Main Page "
                                action={{ type: "internal", route: "", label: "Go to Main Page" }}
                            />
                        </Grid>
                    </Grid>
                </SuiBox>
            </SuiBox>

            <SuiBox my={3}>
                <Card>
                    <DataTable table={datatable1} />
                </Card>
            </SuiBox>

            <SuiBox>
                <Card id="change-password">
                    <SuiBox >
                        <SuiTypography variant="h4" fontWeight="medium" style={{ marginLeft: "20px", margin: "20px" }}>
                            Clients who have not invested in ELSS
                        </SuiTypography>
                        <DataTable table={columns} />
                    </SuiBox>
                </Card>
            </SuiBox>
            <Footer />
        </DashboardLayout>
    );
}

export default Opportunity;
