import { Table, Nav, Container, Navbar , Form,Row,Col } from 'react-bootstrap';


function EditProduct() {

  return (
    <DashboardLayout>
      {/* <DashboardNavbar /> */}

      <Navbar bg="primary" variant="dark">
        <Container>
          <Navbar.Brand href="#home">Admin Home</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="#home">Schedule Reports Management</Nav.Link>
            <Nav.Link href="#features">Schedule Automated Services</Nav.Link>
            <Nav.Link href="#pricing">Scheduler Logs</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <br />
      <SuiTypography variant="h3" fontWeight="medium" style={{ color: "green", textAlign: 'center' }}>
        Scheduled Reports Management
      </SuiTypography>
      <br />
      <Row className="mb-3" style={{fontWeight:'bold'}}>

        <Form.Group as={Col} controlId="formGridState">
          <Form.Label>Transaction Type</Form.Label>
          <Form.Select defaultValue="Choose...">
            <option>Select Condition</option>
            <option>All Customers</option>
            <option>Customers without any Schedule </option>
            {/* <option>Redemption</option>
            <option>Switch Out</option> */}
          </Form.Select>
          <br />
          <Form.Label>Name/Email/Number/User Id</Form.Label>
          <Form.Control type="email" placeholder="Name/Email/Number/User Id" />
          <br />
        </Form.Group>
      </Row>
      <SuiBox my={3}>
        <hr />
