/**
=========================================================
* Soft UI Dashboard PRO React - v2.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/soft-ui-dashboard-pro-material-ui
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

// prop-types is a library for typechecking of props
import PropTypes from "prop-types";

// @mui material components
import Card from "@mui/material/Card";
import Switch from "@mui/material/Switch";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
import SuiTypography from "components/SuiTypography";

function ControllerCard({ color, state, icon, title, description, onChange }) {
  return (
    <Card className="h-100">
      <SuiBox
        p={3}
        height="100%"
        backgroundColor={state ? color : "white"}
        backgroundGradient={state}
        display="flex"
        flexDirection="column"
        justifyContent="space-between"
      >
        <SuiBox
          display="flex"
          justifyContent="space-between"
          alignItems="center"
          mb={2}
          lineHeight={1}
        >
          <SuiTypography variant="body2" textColor={state ? "white" : "text"}>
            {state ? "On" : "Off"}
          </SuiTypography>
          <SuiBox mr={1}>
            <Switch checked={state} onChange={onChange} />
          </SuiBox>
        </SuiBox>
        {icon}
        <SuiBox mt={1} lineHeight={1}>
          <SuiTypography
            variant="body2"
            textColor={state ? "white" : "text"}
            textTransform="capitalize"
            fontWeight="medium"
          >
            {title}
          </SuiTypography>
          {description ? (
            <SuiTypography variant="caption" textColor={state ? "white" : "text"}>
              {description}
            </SuiTypography>
          ) : null}
        </SuiBox>
      </SuiBox>
    </Card>
  );
}

// Setting default values for the props of ControllerCard
ControllerCard.defaultProps = {
  color: "info",
  state: false,
  description: "",
};

// Typechecking props for the ControllerCard
ControllerCard.propTypes = {
  color: PropTypes.oneOf(["primary", "secondary", "info", "success", "warning", "error", "dark"]),
  state: PropTypes.bool,
  icon: PropTypes.node.isRequired,
  title: PropTypes.string.isRequired,
  description: PropTypes.string,
  onChange: PropTypes.func.isRequired,
};

export default ControllerCard;
