import Grid from "@mui/material/Grid";
// import Icon from "@mui/material/Icon";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
// import SuiTypography from "components/SuiTypography";

// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import { Table } from 'react-bootstrap';

// Soft UI Dashboard PRO React base styles
import { Link } from "react-router-dom";


function Default() {

    return (
        <DashboardLayout>
            <DashboardNavbar />

            <h2 style={{ color: 'green', textAlign: 'center' }}>ELSS Opportunity Report</h2>

            <br />

            <hr style={{ color: 'red' }} />
            <br />
            <h5>*This report shows ELSS investment Opportunity available with all the KYC verified users who have invested less than 80C limit in current year till date.
            </h5>

            <SuiBox py={3}>
                <Grid container>
                    <Grid item xs={12} md={10} lg={12} py={3}>
                        <Grid item xs={15} lg={12}>
                            <SuiBox mb={3}>

                                <Table striped bordered hover size="sm" style={{ textAlign: "center", borderColor: 'black' }}>
                                    <thead>

                                        <tr>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Investor Name</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>	BSE UCC</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>PAN No</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Total Amount</th>

                                        </tr>
                                    </thead>

                                    <tbody>

                                        <tr>

                                            <td style={{ padding: '15px' }}><Link to="/authentication/error/anuragdetails" >Anukriti Bhattad</Link></td>


                                            <td style={{ padding: '15px' }}>	A1820</td>

                                            <td style={{ padding: '15px' }}><Link to="/pages/projects/timeline" >	CKSPB1846H</Link></td>

                                            <td style={{ padding: '15px' }}>10,000</td>

                                        </tr>

                                    </tbody>
                                </Table>
                            </SuiBox>
                        </Grid>
                    </Grid>
                </Grid>
            </SuiBox>
            <Footer />
        </DashboardLayout>
    );
}

export default Default;
