import { useState, useEffect } from "react";
// prop-types is a library for typechecking of props
import PropTypes from "prop-types";
// @mui material components
import Grid from "@mui/material/Grid";
import AppBar from "@mui/material/AppBar";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";

// Soft UI Dashboard PRO React base styles
import breakpoints from "assets/theme/base/breakpoints";
import General from "layouts/pages/projects/general";
import Timeline from "layouts/pages/projects/timeline";
// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import { Link } from "react-router-dom";

function Camera ({ stickyNavbar, children }) {

    const [tabsOrientation, setTabsOrientation] = useState("horizontal");
    const [tabValue, setTabValue] = useState(0);

    onst[tabsOrientation, setTabsOrientation] = useState("horizontal");

    const [camera, setCamera] = useState(0);

    useEffect(() => {
        // A function that sets the orientation state of the tabs.
        function handleTabsOrientation() {
            return window.innerWidth < breakpoints.values.md
                ? setTabsOrientation("vertical")
                : setTabsOrientation("horizontal");
        }

        /** 
         The event listener that's calling the handleTabsOrientation function when resizing the window.
        */
        window.addEventListener("resize", handleTabsOrientation);

        // Call the handleTabsOrientation function to set the state with the initial value.
        handleTabsOrientation();

        // Remove event listener on cleanup
        return () => window.removeEventListener("resize", handleTabsOrientation);
    }, [tabsOrientation]);

    const handleSetCamera = (event, newCamera) => setCamera(newCamera);

    useEffect(() => {
        // A function that sets the orientation state of the tabs.
        function handleTabsOrientation() {
            return window.innerWidth < breakpoints.values.sm
                ? setTabsOrientation("vertical")
                : setTabsOrientation("horizontal");
        }

        /** 
         The event listener that's calling the handleTabsOrientation function when resizing the window.
        */
        window.addEventListener("resize", handleTabsOrientation);

        // Call the handleTabsOrientation function to set the state with the initial value.
        handleTabsOrientation();

        // Remove event listener on cleanup
        return () => window.removeEventListener("resize", handleTabsOrientation);
    }, [tabsOrientation]);

    const handleSetTabValue = (event, newValue) => setTabValue(newValue);

    return (

        <DashboardLayout>
            <DashboardNavbar absolute={!stickyNavbar} isMini />
            <SuiBox>
                <SuiBox mt={stickyNavbar ? 3 : 10}>
                    <Grid container p={1}>
                        <Grid item xs={10} sm={12} lg={12}>
                            <AppBar position="static">
                                <Tabs orientation={tabsOrientation} value={tabValue} onChange={handleSetTabValue} >
                                    <Tab label=" AUM (RTA Wise)" style={{ fontWeight: "bold", fontSize: "18px" }} />

                                    <Tab label="AUM (AMC Wise)" style={{ fontWeight: "bold", fontSize: "18px" }}> <Link to="/holding/ amcwiseaum">AUM (AMC Wise)</Link></Tab>

                                    <Tab label="AUM (User Wise)" style={{ fontWeight: "bold", fontSize: "18px" }} />

                                    <Tab label="AUM (Scheme Type Wise)" style={{ fontWeight: "bold", fontSize: "18px" }} />

                                    <Tab label="AUM (Scheme Wise) " style={{ fontWeight: "bold", fontSize: "18px" }} />


                                </Tabs>
                            </AppBar>
                        </Grid>
                    </Grid>
                    {children}
                </SuiBox>
            </SuiBox>
            <SuiBox p={1} mt={1} width="100%" height="25rem">
                <General />
                <Timeline />
            </SuiBox>
            <Footer />
        </DashboardLayout>
    );
}

// Setting default values for the props of BaseLayout
BaseLayout.defaultProps = {
    stickyNavbar: false,
};

// Typechecking props for BaseLayout
BaseLayout.propTypes = {
    stickyNavbar: PropTypes.bool,
    children: PropTypes.node.isRequired,
};

export default Camera;

