// import { useState } from "react";

// @mui material components
import Grid from "@mui/material/Grid";
// import Icon from "@mui/material/Icon";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
import SuiTypography from "components/SuiTypography";

// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
// import DashboardNavbar from "examples/Navbars/DashboardNavbar";
// import Footer from "examples/Footer";
import MiniStatisticsCard from "examples/Cards/StatisticsCards/MiniStatisticsCard";

import { Table } from 'react-bootstrap';

function Widgets() {
  // const [lights, setLights] = useState(false);

  // const handleSetLights = () => setLights(!lights);

  return (

    <DashboardLayout>
      {/* <DashboardNavbar /> */}
      <SuiBox py={1}>
        <Grid container>
          <Grid item xs={12} lg={12}>
            <SuiBox mb={3} p={1}>
              <SuiTypography variant="h4" fontWeight="large" fontSize="1.6rem">
                <h4>
                  AUM (AMC Wise)
                  <a href="/holding/ amcwiseaum">AUM (RTA wise)</a>
                  <a href="/holding/ userwiseaum">AUM (User Wise) </a>
                  <a href="/holding/ schemety.peamc"> AUM (Scheme Type Wise)</a>
                  <a href="/holding/ schemewise"> AUM (Scheme Wise)  </a>
                </h4>
              </SuiTypography>
            </SuiBox>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={4}>
                <SuiBox mb={3} textAlign="center">
                  <MiniStatisticsCard
                    title={{ text: "AMC" }}
                    // count="Rs.53,000"
                    percentage={{ color: "success", text: "" }}
                    icon={{ color: "", component: "" }}
                    backgroundColor="Tomato;"
                  />
                </SuiBox>
                {/* <MiniStatisticsCard
                title={{ text: "Valuation" }}
                count="Rs. 2,300"
                percentage={{ color: "success", text: "" }}
                icon={{ color: "", component: "" }}
              // backgroundColor="#ffccff;"
              /> */}
              </Grid>
              <Grid item xs={12} sm={4}>
                <SuiBox mb={3} textAlign="center" boxsize="5px">
                  <MiniStatisticsCard
                    title={{ text: "Invested Amount" }}
                    // count="Rs.53,000"
                    percentage={{ color: "success", text: "" }}
                    icon={{ color: "", component: "" }}
                    backgroundColor="#038d8a;"
                  />
                </SuiBox>
                {/* <MiniStatisticsCard
                title={{ text: "Valuation" }}
                count="Rs. 2,300"
                percentage={{ color: "success", text: "" }}
                icon={{ color: "", component: "" }}
              // backgroundColor="#e6f3ff;"
              /> */}
              </Grid>
              <Grid item xs={12} sm={4}>
                <SuiBox mb={3} textAlign="center">
                  <MiniStatisticsCard
                    title={{ text: "AUM" }}
                    // count="Rs.3,462"
                    percentage={{ color: "error", text: "" }}
                    icon={{ color: "", component: "" }}
                    backgroundColor="#e8206a;"
                  />
                </SuiBox>
                {/* <SuiBox mb={3}>
                <MiniStatisticsCard
                  title={{ text: "Total Div Rein." }}
                  count="Rs.103,430"
                  percentage={{ color: "success", text: "" }}
                  icon={{
                    color: "",
                    component: "",
                  }}
                backgroundColor="#1798A5;"
                />
              </SuiBox> */}
              </Grid>
            </Grid>
          </Grid>
          <Grid item xs={15} lg={12} py={.05}>
            <SuiBox mb={3}>
              <Table striped bordered hover size="sm" style={{ textAlign: "center", borderColor: 'black' }}>
                <thead>
                <tr>
                  <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>AMC</th>
                  <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Invested Amount</th>
                  <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>AUM</th>
                </tr>
              </thead>
                <tbody>
                  <tr>
                    <td style={{ Color: "#038d8a", padding: "10px" }}>Aditya Birla Sun Life Mutual Fund</td>
                    <td>5,72,45,790</td>
                    <td>7,30,92,892</td>
                  </tr>
                  <tr>
                    <td style={{ Color: "#038d8a", padding: "10px" }}>ICICI Prudential Mutual Fund</td>
                    <td>4,37,75,594</td>
                    <td>4,76,04,994</td>
                  </tr>
                  <tr>
                    <td style={{ Color: "#038d8a", padding: "10px" }}>UTI Mutual Fund</td>
                    <td>1,47,60,680</td>
                    <td>1,72,64,262</td>
                  </tr>
                  <br />

                </tbody>
                <tr>
                    <td style={{ fontWeight: "900", backgroundColor: "#ddd" , padding:"10px"}}> Total</td>
                    <td style={{ fontWeight: "900", backgroundColor: "#ddd" }}>15,33,16,151</td>
                    <td style={{ fontWeight: "900", backgroundColor: "#ddd" }}>Nill</td>
                  </tr>
              </Table>
            </SuiBox>
          </Grid>
        </Grid>
      </SuiBox>
    </DashboardLayout>
  );
}

export default Widgets;
