/**
=========================================================
* Soft UI Dashboard PRO React - v2.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/soft-ui-dashboard-pro-material-ui
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

// @mui material components
import Grid from "@mui/material/Grid";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
import SuiTypography from "components/SuiTypography";
import SuiButton from "components/SuiButton";
import Card from "@mui/material/Card";
// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";

// EditProduct page components
// import ProductImage from "layouts/ecommerce/products/edit-product/components/ProductImage";
// import ProductInfo from "layouts/ecommerce/products/edit-product/components/ProductInfo";
// import Socials from "layouts/ecommerce/products/edit-product/components/Socials";
// import Pricing from "layouts/ecommerce/products/edit-product/components/Pricing";
import { Table } from 'react-bootstrap';
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import Divider from "@mui/material/Divider";
import DataTable from "examples/Tables/DataTable";
import dataTableData from "layouts/ecommerce/orders/order-list/data/dataTableData";

function EditProduct() {

  const [menu, setMenu] = useState(null);

  // const openMenu = (event) => setMenu(event.currentTarget);
  const closeMenu = () => setMenu(null);

  const renderMenu = (
    <Menu
        anchorEl={menu}
        anchorOrigin={{ vertical: "bottom", horizontal: "left" }}
        transformOrigin={{ vertical: "top", horizontal: "left" }}
        open={Boolean(menu)}
        onClose={closeMenu}
        keepMounted
    >
        <MenuItem onClick={closeMenu}>Status: Paid</MenuItem>
        <MenuItem onClick={closeMenu}>Status: Refunded</MenuItem>
        <MenuItem onClick={closeMenu}>Status: Canceled</MenuItem>
        <Divider sx={{ margin: "0.5rem 0" }} />
        <MenuItem onClick={closeMenu}>
            <SuiTypography variant="button" textColor="error" fontWeight="regular">
                Remove Filter
            </SuiTypography>
        </MenuItem>
    </Menu>
);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <SuiBox my={3}>
        <SuiBox mb={3}>
          <Grid container spacing={3} alignItems="center">
            <Grid item xs={12} lg={6}>
              <SuiTypography variant="h4" fontWeight="medium">
                Email Login Details to Clients
              </SuiTypography>

            </Grid>

            <Grid item xs={12} lg={6}>
              <SuiBox display="flex" justifyContent="flex-end">
                <SuiButton variant="gradient" buttonColor="info">
                  Apply
                </SuiButton>
              </SuiBox>
            </Grid>
          </Grid>
        </SuiBox>
        <hr />
        <Grid container spacing={1}>

          <Table striped bordered hover size="sm" style={{ textAlign: "center" }}>
            <thead>
              <tr>
                <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Name</th>
                <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Email Id</th>
                <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Mobile</th>
                <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>
                  <leble className="from-check-lable ms-2">Select</leble>
                  <input type="checkbox" className="from-check-input" />
                </th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td style={{ Color: "#038d8a", padding: "10px" }}><a href="/user/view/4" target="_blank">Anuraag Saboo(4)</a></td>
                <td>anuraag@sheltron.com</td>
                <td>9880272472</td>
                <td><input type="checkbox" className="from-check-input" /></td>
              </tr>
              <tr>
                <td style={{ Color: "#038d8a", padding: "10px" }}><a href="/user/view/4" target="_blank">Anuraag Saboo(4)</a></td>
                <td>anuraag@sheltron.com</td>
                <td>9880272472</td>
                <td><input type="checkbox" className="from-check-input" /></td>
              </tr>
              <tr>
                <td style={{ Color: "#038d8a", padding: "10px" }}><a href="/user/view/4" target="_blank">Anuraag Saboo(4)</a></td>
                <td>anuraag@sheltron.com</td>
                <td>9880272472</td>
                <td><input type="checkbox" className="from-check-input" /></td>
              </tr>
            </tbody>
          </Table>
        </Grid>

        <SuiBox display="flex" justifyContent="space-between" alignItems="flex-start" mb={2} >
          {/* <SuiButton variant="gradient" buttonColor="info">
                            new order
                        </SuiButton> */}
          <SuiBox display="flex">
            {/* <SuiButton
                                variant={menu ? "contained" : "outlined"}
                                buttonColor="dark"
                                onClick={openMenu}
                            >
                                filters&nbsp;
                                <Icon className="">keyboard_arrow_down</Icon>
                            </SuiButton> */}
            {renderMenu}
            <SuiBox ml={1}>
              {/* <SuiButton variant="outlined" buttonColor="dark">
                                    <Icon className="">description</Icon>
                                    &nbsp;export csv
                                </SuiButton> */}
            </SuiBox>
          </SuiBox>
        </SuiBox>
        <Card style={{ fontWeight: "bold" }}>
          <DataTable table={dataTableData} entriesPerPage={false} canSearch />
        </Card>
      </SuiBox>
      <Footer />
    </DashboardLayout>
  );
}

export default EditProduct;
