import Grid from "@mui/material/Grid";
// import Icon from "@mui/material/Icon";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
// import SuiTypography from "components/SuiTypography";

// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import { Table } from 'react-bootstrap';

// Soft UI Dashboard PRO React base styles
import { Link } from "react-router-dom";


function Default() {

    return (
        <DashboardLayout>
            <DashboardNavbar />

            <h2 style={{ color: 'green', textAlign: 'center' }}>ELSS Lock-in free Report</h2>

            <br />

            <hr style={{ color: 'red' }} />


            <SuiBox py={3}>
                <Grid container>
                    <Grid item xs={12} md={10} lg={12} py={3}>
                        <Grid item xs={15} lg={12}>
                            <SuiBox mb={3}>

                                <h5>* This report shows the units invested in ELSS funds which are free from Lock-in period of 3 years, for each user and can now be switched into other suitable fund choices.
                                </h5>


                                <h3 style={{ color: 'brown' }}>Internal Holdings</h3>

                                <hr style={{ color: 'red' }} />
                                <Table striped bordered hover size="sm" style={{ textAlign: "center", borderColor: 'black' }}>
                                    <thead>

                                        <tr>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Investor Name</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>PAN No</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Scheme</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Folio No.</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Free Units</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Current Amount</th>

                                            <th style={{ backgroundColor: "rgb(249, 221, 171)", padding: "15px" }}>Invested Amount</th>

                                        </tr>
                                    </thead>

                                    <tbody>

                                        <tr>

                                            <td style={{ padding: '15px' }}><Link to="/authentication/error/anuragdetails" >ABHA AGARWAL</Link></td>

                                            <td style={{ padding: '15px' }}><Link to="/authentication/error/anurag" >	AEGPA6692G</Link></td>

                                            <td style={{ padding: '15px' }}>		1362.249</td>

                                            <td style={{ padding: '15px' }}>95,973</td>
                                         
                                           <td style={{ padding: '15px' }}>75,000</td>                                  

                                        </tr>

                                    </tbody>
                                </Table>


                            </SuiBox>
                        </Grid>
                    </Grid>
                </Grid>
            </SuiBox>
            <Footer />
        </DashboardLayout>
    );
}

export default Default;
