import { useState, useEffect } from "react";
import DataTable from "examples/Tables/DataTable";
// @mui material components
import Card from "@mui/material/Card";
import AppBar from "@mui/material/AppBar";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
// import rtatable1 from "layouts/authentication/error/rtatable1";
import SuiBox from "components/SuiBox";

// Soft UI Dashboard PRO React base styles
import breakpoints from "assets/theme/base/breakpoints";
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
// Soft UI Dashboard PRO React example components
import CameraView from "layouts/dashboards/smart-home/components/CameraView";
// Images
import SweetAlerts from "layouts/pages/sweet-alerts";
import Notifications from "layouts/pages/notifications";
import camera3 from "assets/images/home-decor-3.jpg";

import Grid from "@mui/material/Grid";
import SuiTypography from "components/SuiTypography";

function Cameras() {
  const [tabsOrientation, setTabsOrientation] = useState("horizontal");

  const [camera, setCamera] = useState(0);

  useEffect(() => {
    // A function that sets the orientation state of the tabs.
    function handleTabsOrientation() {
      return window.innerWidth < breakpoints.values.md
        ? setTabsOrientation("vertical")
        : setTabsOrientation("horizontal");
    }

    /** 
     The event listener that's calling the handleTabsOrientation function when resizing the window.
    */
    window.addEventListener("resize", handleTabsOrientation);

    // Call the handleTabsOrientation function to set the state with the initial value.
    handleTabsOrientation();

    // Remove event listener on cleanup
    return () => window.removeEventListener("resize", handleTabsOrientation);
  }, [tabsOrientation]);

  const handleSetCamera = (event, newCamera) => setCamera(newCamera);

  const Columns =
  {

    columns: [
      { Header: <h5 style={{ color: "black" }}>Client Name</h5>, accessor: "InvestorName", },

      { Header: <h5 style={{ color: "black" }}>Pan Number</h5>, accessor: "Location" },

      { Header: <h5 style={{ color: "black" }}>STP Amount</h5>, accessor: "Investments" },

      { Header: <h5 style={{ color: "black" }}>No. Of STPS</h5>, accessor: "Invest" },
    ],

    rows: [
      {
        InvestorName: <h8 style={{ color: "black" }}>Anil Kumar Rathi</h8>,

        Location: <h8 style={{ color: "black" }}>AASPR7023C</h8>,

        Investments: <h8 style={{ color: "black" }}>1,00,000</h8>,

        Invest: <h8 style={{ color: "black" }}>2</h8>,
      },

      {
        InvestorName: <h8 style={{ color: "black" }}>Rajiv Taparia</h8>,

        Location: <h8 style={{ color: "black" }}>ACGPT7191P</h8>,

        Investments: <h8 style={{ color: "black" }}>60,000</h8>,

        Invest: <h8 style={{ color: "black" }}>3</h8>,
      },


      {
        InvestorName: <h8 style={{ color: "black" }}>Ranjan Jaiswal</h8>,

        Location: <h8 style={{ color: "black" }}>ADGPJ4854J</h8>,

        Investments: <h8 style={{ color: "black" }}>50,000</h8>,

        Invest: <h8 style={{ color: "black" }}>2</h8>,
      },


      {
        InvestorName: <h8 style={{ color: "black" }}>Khushboo Taparia Bed</h8>,

        Location: <h8 style={{ color: "black" }}>AGWPT5388C</h8>,

        Investments: <h8 style={{ color: "black" }}>13,000</h8>,

        Invest: <h8 style={{ color: "black" }}>2</h8>,
      },

      {
        InvestorName: <h5 style={{ color: "green" }}>Total</h5>,
        Location: "",
        Investments: <h5 style={{ color: "green" }}>2,23,000</h5>,
        Invest: <h5 style={{ color: "green" }}>9</h5>,
      },
    ],

  };



  return (

    <DashboardLayout>
      <DashboardNavbar />

      <SuiBox p={3} style={{ backgroundColor: ' #EFFFFD', borderRadius: '15px', marginTop: "10px" }} >
        <Grid container spacing={3} alignItems="center">
          <Grid item>
            <SuiBox height="100%" mt={0.5} lineHeight={2}>
              <SuiTypography variant="h4" fontWeight="bold" style={{ color: "black", textAlign: 'center' }}>
                STP Reports
              </SuiTypography>
            </SuiBox>
          </Grid>
        </Grid>
      </SuiBox>

      <Card style={{ backgroundColor: ' #FE8F8F', marginTop: "10px" }} >

      <SuiBox height="100%" mt={0.5} lineHeight={2}>
          <SuiTypography variant="h4" fontWeight="bold" style={{ color: "black", textAlign: 'center' }}>
          Client Wise STP
          </SuiTypography>
        </SuiBox>

        <SuiBox display="flex" justifyContent="space-between" alignItems="center" pt={2} px={2}>

          <SuiBox display="flex" justifyContent="space-between" alignItems="center" width="100%">
            <SuiBox width="100%">
              <AppBar position="static">
                <Tabs orientation={tabsOrientation} value={camera} onChange={handleSetCamera}>
                  <Tab label="AMC Wise STP" style={{ fontWeight: "bold", fontSize: "18px", margin: '10px' }} />

                  <Tab label="Scheme Wise STP" style={{ fontWeight: "bold", fontSize: "18px", margin: '10px' }} />

                  <Tab label="STP Maturing in 1 Month" style={{ fontWeight: "bold", fontSize: "18px", margin: '10px' }} />

                  <Tab label="STP Maturing in Last 1 Month" style={{ fontWeight: "bold", fontSize: "18px", margin: '10px' }} />
                </Tabs>
              </AppBar>
            </SuiBox>

          </SuiBox>
        </SuiBox>

        <SuiBox p={2} mt={1} width="100%" height="240rem">
          <SweetAlerts/>
          <Notifications />
          <CameraView image={camera3} date="17.05.2021" time="4:57PM" value={camera} index={2} />
        </SuiBox>
      </Card>

      <SuiBox p={1} style={{ backgroundColor: ' #FCD2D1', borderRadius: '15px', marginTop: "10px" }}>
        <Card id="change-password">
          <SuiBox  >
            <DataTable table={Columns} entriesPerPage={false} canSearch />
          </SuiBox>
        </Card>
      </SuiBox>

    </DashboardLayout>
  );
}

export default Cameras;
