import Grid from "@mui/material/Grid";
import SuiTypography from "components/SuiTypography";
import SuiBox from "components/SuiBox";
// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
// import Footer from "examples/Footer";
import Card from "@mui/material/Card";
import Checkbox from "@mui/material/Checkbox";
import SuiButton from "components/SuiButton";
import PropTypes from "prop-types";
import 'bootstrap/dist/css/bootstrap.min.css';
// import ProductCell from "layouts/ecommerce/products/product-page/components/ProductCell";
import IdCell from "layouts/ecommerce/orders/order-list/components/IdCell";
import DataTable from "examples/Tables/DataTable";
import breakpoints from "assets/theme/base/breakpoints";
// Images
import team4 from "assets/images/team-4.jpg";
// import { Link } from "react-router-dom";
import SuiEditor from "components/SuiEditor";
import { useState } from "react";

import DefaultCell from "layouts/ecommerce/orders/order-list/components/DefaultCell";
import StatusCell from "layouts/ecommerce/orders/order-list/components/StatusCell";
import CustomerCell from "layouts/ecommerce/orders/order-list/components/CustomerCell";

// Images
import team1 from "assets/images/team-1.jpg";
import team2 from "assets/images/team-2.jpg";
import team3 from "assets/images/team-3.jpg";


function Overview({ id, checked }) {

    const [editorValue, setEditorValue] = useState(
        "<p>Hello World!</p><p>Some initial <strong>bold</strong> text</p><br><br>"
    );

    const { values } = breakpoints;
    const columns =
    {
        // columns: [
        //     { Header: "	Name", accessor: "product" },     
        //     { Header: "Id", accessor: "bse" },
        //     { Header: "Email", accessor: "pan" },
        //     { Header: "Mobile", accessor: "a1" },
        //     { Header: "id", accessor: "id", Cell: ({ value }) => <IdCell id={value} /> },
            
      
        // ],

        // rows: [
        //     {
        //         product: <Link to="/authentication/error/user"> <ProductCell image={team4} name="Anil Kumar Rathi" /></Link>,
        //         bse: "Aug 19, 2022",
        //         pan: "RTA",
        //         a1: "A182040",
        //         id: "#10421",
        //     },
        // ],

        columns: [
            { Header: "id", accessor: "id", Cell: ({ value }) => <IdCell id={value} /> },
            {
              Header: "date",
              accessor: "date",
              Cell: ({ value }) => <DefaultCell value={value} />,
            },
            {
              Header: "status",
              accessor: "status",
              Cell: ({ value }) => {
                let status;
        
                if (value === "paid") {
                  status = <StatusCell icon="done" color="success" status="Paid" />;
                } else if (value === "refunded") {
                  status = <StatusCell icon="replay" color="dark" status="Refunded" />;
                } else {
                  status = <StatusCell icon="close" color="error" status="Canceled" />;
                }
        
                return status;
              },
            },
            {
              Header: "customer",
              accessor: "customer",
              Cell: ({ value: [name, data] }) => (
                <CustomerCell image={data.image} color={data.color || "dark"} name={name} />
              ),
            },
            {
              Header: "product",
              accessor: "product",
              Cell: ({ value }) => {
                const [name, data] = value;
        
                return (
                  <DefaultCell
                    value={typeof value === "string" ? value : name}
                    suffix={data.suffix || false}
                  />
                );
              },
            },
            { Header: "revenue", accessor: "revenue", Cell: ({ value }) => <DefaultCell value={value} /> },
          ],
        
          rows: [
            {
              id: "#10421",
              date: "1 Nov, 10:20 AM",
              status: "paid",
              customer: ["Orlando Imieto", { image: team2 }],
              product: "Nike Sport V2",
              revenue: "$140,20",
            },
            {
              id: "#10422",
              date: "1 Nov, 10:53 AM",
              status: "paid",
              customer: ["Alice Murinho", { image: team1 }],
              product: "Valvet T-shirt",
              revenue: "$42,00",
            },
            {
              id: "#10423",
              date: "1 Nov, 11:13 AM",
              status: "refunded",
              customer: ["Michael Mirra", { image: "M" }],
              product: ["Leather Wallet", { suffix: "+1 more" }],
              revenue: "$25,50",
            },
            {
              id: "#10424",
              date: "1 Nov, 12:20 PM",
              status: "paid",
              customer: ["Andrew Nichel", { image: team3 }],
              product: "Bracelet Onu-Lino",
              revenue: "$19,40",
            },
           
          ],

    }

   


    Overview.defaultProps = {
        checked: false,
    };

    // Typechecking props for the IdCell
    Overview.propTypes = {
        id: PropTypes.string.isRequired,
        checked: PropTypes.bool,
    };

    const passwordRequirements = [

    ];

    const renderPasswordRequirements = passwordRequirements.map((item, key) => {
        const itemKey = `element-${key}`;

        return (

            <SuiBox key={itemKey} component="li" color="text" fontSize="1.25rem" lineHeight={1}>
                <SuiTypography
                    variant="button"
                    textColor="text"
                    fontWeight="regular"
                    verticalAlign="middle"
                >
                    {item}
                </SuiTypography>
            </SuiBox>
        );
    });


    return (

        <DashboardLayout>
            <DashboardNavbar />

            <SuiBox my={3}>
                <Card id="change-password">

                    <SuiBox mb={3} p={3} style={{ backgroundColor: "#f8f9fa" }}>
                        <SuiTypography
                            variant={window.innerWidth < values.sm ? "h3" : "h2"}
                            textTransform="capitalize"
                            fontWeight="bold"
                        >
                            Send App Notification
                        </SuiTypography>
                    </SuiBox>

                    <SuiBox display="flex" justifyContent="space-between" alignItems="flex-end" flexWrap="wrap">
                        <SuiBox component="ul" m={0} pl={3.25} mb={{ xs: 8, sm: 0 }}>
                            {renderPasswordRequirements}
                        </SuiBox>

                    </SuiBox>

                    <SuiBox component="form" pb={3} px={3} p={3} mb={10}>

                        <SuiBox
                            display="flex"
                            flexDirection="column"
                            justifyContent="flex-end"
                            height="100%"
                        >
                            <SuiBox mb={1} ml={0.5} mt={3} lineHeight={0} display="inline-block">
                                <SuiTypography component="label" variant="h5" fontWeight="bold">
                                    Text to send in Notification
                                </SuiTypography>
                            </SuiBox>
                            <SuiEditor value={editorValue} onChange={setEditorValue} />
                        </SuiBox>

                        <Grid container spacing={3}>

                            <Grid item xs={12}>
                                <Grid container spacing={2}>

                                    <Grid item xs={12} sm={12}>
                                        <Grid container spacing={3}>

                                            <Grid item xs={12} sm={4}>
                                                <SuiBox display="flex" alignItems="center" style={{ marginTop: "30px" }}>

                                                    <Checkbox defaultChecked={checked} />
                                                    <SuiBox ml={1}>
                                                        <SuiTypography variant="caption" fontWeight="medium" textColor="text">
                                                            {id}
                                                        </SuiTypography>
                                                    </SuiBox>

                                                    <SuiTypography component="label" variant="h7" fontWeight="bold">
                                                        Send App Notification
                                                    </SuiTypography>
                                                </SuiBox>
                                            </Grid>

                                            <Grid item xs={12} sm={4}>
                                                <SuiBox display="flex" alignItems="center" style={{ marginTop: "30px" }}>

                                                    <Checkbox defaultChecked={checked} />
                                                    <SuiBox ml={1}>
                                                        <SuiTypography variant="caption" fontWeight="medium" textColor="text">
                                                            {id}
                                                        </SuiTypography>
                                                    </SuiBox>

                                                    <SuiTypography component="label" variant="h7" fontWeight="bold">
                                                        Download CSV
                                                    </SuiTypography>
                                                </SuiBox>
                                            </Grid>

                                            <Grid item xs={12} sm={4}>
                                                <SuiBox
                                                    display="flex"
                                                    flexDirection="column"
                                                    justifyContent="flex-end"
                                                    height="100%"
                                                >
                                                    <SuiButton variant="gradient" buttonColor="dark">
                                                        Submit
                                                    </SuiButton>
                                                </SuiBox>
                                            </Grid>
                                        </Grid>

                                    </Grid>

                                </Grid>
                            </Grid>
                        </Grid>
                    </SuiBox>
                </Card>

                <SuiBox component="form" my={3}>
                    <Card id="change-password" style={{ backgroundColor: "#f8f9fa" }}>
                        <SuiBox mt={8} mb={2}>
                            <SuiBox mb={1} ml={2}>
                                <SuiTypography variant="h5" fontWeight="medium">
                                    Monthly Outflow (Aug 2022)
                                </SuiTypography>
                            </SuiBox>
                            <DataTable
                                table={columns}
                                entriesPerPage={false}
                                showTotalEntries={false}
                                isSorted={false}
                            />
                        </SuiBox>
                    </Card>
                </SuiBox>
            </SuiBox>
        </DashboardLayout>
    );
}

export default Overview;
