/**
=========================================================
* Soft UI Dashboard PRO React - v2.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/soft-ui-dashboard-pro-material-ui
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

// @mui material components
import Card from "@mui/material/Card";

// Soft UI Dashboard PRO React components
import SuiBox from "components/SuiBox";
import SuiTypography from "components/SuiTypography";

// Soft UI Dashboard PRO React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import DataTable from "examples/Tables/DataTable";
// Data
import dataTableData from "layouts/ecommerce/referral/data/dataTableData";
import breakpoints from "assets/theme/base/breakpoints";

// // Overview page components
// import ProductCell from "layouts/ecommerce/overview/components/ProductCell";
// import RefundsCell from "layouts/ecommerce/overview/components/RefundsCell";
// import DefaultCell from "layouts/ecommerce/overview/components/DefaultCell";

// // Images
// import team1 from "assets/images/team-1.jpg";
// import team2 from "assets/images/team-2.jpg";
// import team3 from "assets/images/team-3.jpg";
// import team4 from "assets/images/team-4.jpg";
// import team5 from "assets/images/team-5.jpg";

// Countries flags
import india from "assets/images/icons/flags/india.png";
import ind from "assets/images/icons/flags/ind.png";
import ind1 from "assets/images/icons/flags/ind1.png";
import ind2 from "assets/images/icons/flags/ind2.png";

function Referral() {
    const { values } = breakpoints;

    const Columns =
    {

        columns: [
            {
                AMC: [india, "Aditya Birla Sun Life Mutual Fund"],
                investments: "Rs. 7,38,08,920",
            },
            {
                AMC: [ind, "ICICI Prudential Mutual Fund"],
                investments: "Rs. 4,74,09,695",
            },
            {
                AMC: [ind1, "UTI Mutual Fund"],
                investments: "Rs. 1,72,20,881",
            },
            {
                AMC: [ind2, "HDFC Mutual Fund"],
                investments: "Rs. 44,01,731",
            },
        ],

        // rows: [
        //     {
        //         user: <ProductCell image={team1} name="AGARWAL BUILDCON" />,
        //         value: <DefaultCell image={team1}>$130.992</DefaultCell>,
        //         profit: <DefaultCell>$9.500</DefaultCell>,
        //     },
        //     {
        //         user: <ProductCell image={team2} name="John Alura" orders={12.821} />,
        //         value: <DefaultCell>$80.250</DefaultCell>,
        //         profit: <DefaultCell>$4.200</DefaultCell>,
        //         refunds: <RefundsCell value={40} icon={{ color: "error", name: "keyboard_arrow_down" }} />,
        //     },
        //     {
        //         user: <ProductCell image={team3} name="Andrew Sian" orders={2.421} />,
        //         value: <DefaultCell>$40.600</DefaultCell>,
        //         profit: <DefaultCell>$9.430</DefaultCell>,
        //         refunds: <RefundsCell value={54} icon={{ color: "success", name: "keyboard_arrow_up" }} />,
        //     },
        //     {
        //         user: <ProductCell image={team4} name="Luca Willaim" orders={5.921} />,
        //         value: <DefaultCell>$91.300</DefaultCell>,
        //         profit: <DefaultCell>$7.364</DefaultCell>,
        //         refunds: <RefundsCell value={5} icon={{ color: "error", name: "keyboard_arrow_down" }} />,
        //     },
        //     {
        //         user: <ProductCell image={team5} name="Richel Manuel" orders={921} />,
        //         value: <DefaultCell>$140.925</DefaultCell>,
        //         profit: <DefaultCell>$20.531</DefaultCell>,
        //         refunds: (
        //             <RefundsCell
        //                 value={121}
        //                 icon={{ color: "success", name: "keyboard_arrow_up" }}
        //                 tooltip="Refund rate is higher with 70% than other products"
        //             />
        //         ),
        //     },
        // ],

    };


    return (
        <DashboardLayout>
            <DashboardNavbar />

            <SuiBox mb={3} p={1}>
                <SuiTypography
                    variant={window.innerWidth < values.sm ? "h3" : "h3"}
                    textTransform="capitalize"
                    fontWeight="bold"
                >
                    ELSS Opportunity Report
                </SuiTypography>
            </SuiBox>
            <SuiBox my={3}>
                <Card>
                    <SuiBox pt={3} px={3}>
                        <SuiTypography variant="h6" fontWeight="medium">
                            *This report shows ELSS investment Opportunity available with all the KYC verified users who have invested less than 80C limit in current year till date.
                        </SuiTypography>
                    </SuiBox>
                    <DataTable
                        table={dataTableData}
                        entriesPerPage={false}
                        showTotalEntries={false}
                        isSorted={false}
                        noEndBorder
                    />
                </Card>
            </SuiBox>

            <SuiBox my={3}>
                <Card>
                    <SuiBox pt={3} px={3}>
                        <SuiTypography variant="h6" fontWeight="medium">
                            Clients who have not invested in ELSS
                        </SuiTypography>
                    </SuiBox>
                    <DataTable
                        table={Columns}
                        entriesPerPage={false}
                        showTotalEntries={false}
                        isSorted={false}
                        noEndBorder
                    />
                </Card>
            </SuiBox>
            <Footer />
        </DashboardLayout>
    );
}

export default Referral;
